import { useBladeNavigation } from '@vc-shell/framework';
import { markRaw } from 'vue';
import ProductDetails from './product-details.vue';

const { openBlade } = useBladeNavigation();

function onItemClick(item: Product) {
  openBlade({
    blade: markRaw(ProductDetails),
    param: item.id,
    onOpen() {
      selectedItemId.value = item.id;
    },
    onClose() {
      selectedItemId.value = undefined;
      reload();
    },
  });
}

import { useBladeNavigation } from '@vc-shell/framework';

const { closeBlade, currentBladeNavigationData } = useBladeNavigation();

async function save() {
  await saveProduct();
  closeBlade(currentBladeNavigationData.value.idx);
}

// Or use emit instead:
emit('close:blade');

import { useBladeNavigation, usePopup } from '@vc-shell/framework';

const { onBeforeClose } = useBladeNavigation();
const { showConfirmation } = usePopup();
const { modified } = useModificationTracker(formData);

onBeforeClose(async () => {
  if (modified.value) {
    return await showConfirmation(
      computed(() => t('ALERTS.UNSAVED_CHANGES'))
    );
  }
});

import { usePopup } from '@vc-shell/framework';
import { useI18n } from 'vue-i18n';

const { showConfirmation } = usePopup();
const { t } = useI18n();

async function deleteItem(item: Product) {
  const confirmed = await showConfirmation(
    computed(() => t('CONFIRM_DELETE', { name: item.name }))
  );

  if (!confirmed) return;

  await api.delete(item.id);
  await reload();
}

