  return products;
});

await loadProducts();

import { useApiClient } from '@vc-shell/framework';
import { ApiClient } from '../api';

const { getApiClient } = useApiClient(ApiClient);

async function searchProducts(query) {
  const client = await getApiClient();
  return await client.searchProducts(query);
}

import { useModificationTracker } from '@vc-shell/framework';

const formData = ref({ name: '', email: '' });
const { modified, currentValue, resetModificationState } = useModificationTracker(formData);

async function save() {
  await api.save(currentValue.value);
  resetModificationState();
}

