import { useBreadcrumbs } from '@vc-shell/framework';
import { useI18n } from 'vue-i18n';
import { computed } from 'vue';

const { t } = useI18n({ useScope: 'global' });
const { push } = useBreadcrumbs();

// Add catalog breadcrumb with data loading handler
push({
  id: 'catalog',
  title: computed(() => t('BREADCRUMBS.CATALOG')),
  clickHandler: async () => {
    await loadCategoryList({ catalogId: sellerId }, sellerId);
    return true; // Remove subsequent breadcrumbs
  }
});

import { useWidgets, useBlade } from '@vc-shell/framework';
import { computed } from 'vue';
import SpecialPricesWidget from './widgets/SpecialPricesWidget.vue';

const { registerWidget, updateActiveWidget } = useWidgets();
const blade = useBlade();
const offer = ref({ priceLists: [] });

registerWidget(
  {
    id: 'SpecialPricesWidget',
    component: SpecialPricesWidget,
    props: { priceLists: computed(() => offer.value.priceLists) },
    updateFunctionName: 'updateActiveWidgetCount',
    isVisible: computed(() => !!props.param)
  },
  blade.value.id
);

// Update widget after save
async function save() {
  await saveOffer();
  updateActiveWidget();
}

